mapper.select:
SELECT 

FROM staff_edu a

WHERE a.id = #{id}

SELECT

FROM staff_edu a

WHERE a.staff_id = #{staffId} AND a.del_flag = '0';

SELECT

FROM staff_edu a


	a.del_flag = '0' And a.staff_id =#{staffId}

SELECT 

FROM staff_edu a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM staff_edu a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE staff_edu SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
