mapper.select:
SELECT 

FROM staff_role_prop a

WHERE a.id = #{id}

SELECT 

FROM staff_role_prop a

WHERE a.del_flag = '0'

	AND a.role_id = #{roleId}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM staff_role_prop a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE staff_role_prop SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.delete:
DELETE FROM staff_role_prop
WHERE role_id = #{roleId}
AND template_id = #{templateId}
