mapper.select:
SELECT 

FROM staff_prop_template a

WHERE a.id = #{id}

SELECT 

FROM staff_prop_template a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM staff_prop_template a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE staff_prop_template SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
